import { Dot } from "lucide-react"

import { ChatMessage, type Message } from "@/components/ui/chat-message"

interface MessageListProps {
  messages: Message[]
  showTimeStamps?: boolean
  isLoading?: boolean
}

export function MessageList({
  messages,
  showTimeStamps = true,
  isLoading = false,
}: MessageListProps) {
  return (
    <div className="space-y-4 overflow-y-auto">
      {messages.map((message, index) => (
        <ChatMessage key={index} showTimeStamp={showTimeStamps} {...message} />
      ))}
      {isLoading && (
        <div className="justify-left flex space-x-1">
          <div className="rounded-lg bg-muted p-3">
            <div className="flex -space-x-2.5">
              <Dot className="h-5 w-5 animate-typing-dot-bounce" />
              <Dot className="h-5 w-5 animate-typing-dot-bounce [animation-delay:90ms]" />
              <Dot className="h-5 w-5 animate-typing-dot-bounce [animation-delay:180ms]" />
            </div>
          </div>
        </div>
      )}
    </div>
  )
}
